import React, { useState, useEffect } from 'react';
import { useNavigation } from '@react-navigation/native';
import { FlatList, TouchableOpacity, View, Text, Image ,TextInput} from 'react-native';
import { ScrollView } from 'react-native-web';

function Content() {
  const navigation = useNavigation();
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");


  useEffect(() => {
    fetch('https://fakestoreapi.com/products')
      .then(res => res.json())
      .then(json => setProducts(json));
  }, []);
  const filteredProducts = products.filter((item) =>
  item.title.toLowerCase().includes(searchTerm.toLowerCase())
);


  return (

    <View style={{ width: '100%', backgroundColor: 'white', flex: 70 }}>
        <ScrollView>
        <View style={styles.searchContainer}>
          <TextInput

            placeholder="Search products..."
            value={searchTerm}
            onChangeText={(text) => setSearchTerm(text)}
          />
        </View>
      <FlatList
        keyExtractor={(item) => item.name}
        numColumns={2}
        data={filteredProducts}
        renderItem={({ item, index }) => (
          <TouchableOpacity
            onPress={() =>
              navigation.navigate('ProductDetail', {
                name: item.title,
                url: item.image,
                price: item.price,
                 // Make sure addToCart is defined
              })
            }

            style={{
              flex: 1,
              marginLeft: index % 2 == 0 ? 8 : 2,
              marginTop: 5,
              marginRight: index % 2 == 0 ? 2 : 8,
              marginBottom: 5,
              borderRadius: 20,
              borderWidth: 1,
              borderColor: 'black',
            }}
          >
            <View style={{ flexDirection: 'row' }}>
              <Image
                source={{ uri: item.image }}
                style={{
                  height: 100,
                  width: 100,
                  borderRadius: 10,
                  margin: 10,
                  resizeMode: 'cover',
                }}
              />
              <Text style={{ color: 'red', marginStart: 10 }}>
                ${item.price}
              </Text>
            </View>
            <Text style={{ color: 'black' }}>{item.title}</Text>
            <View style={{ flex: 1 }}>
              {/* Additional content if needed */}
            </View>
          </TouchableOpacity>
        )}
      />
      </ScrollView>
    </View>
  );
}

export default Content;
