import React from 'react';
import { View, Text, FlatList, Image } from 'react-native';

function Cart({ route }) {
  const { addedItem } = route.params;
  // Cập nhật giỏ hàng hoặc thực hiện các thao tác khác với addedItem

  return (
    <View>
      {/* Hiển thị thông tin giỏ hàng */}
      <Text>Added to Cart:</Text>
      <FlatList
        data={[addedItem]}
        keyExtractor={(item) => item.name}
        renderItem={({ item }) => (
          <View>
            <Text>{item.name}</Text>
            <Text>${item.price}</Text>
            <Image source={{ uri: item.image }} style={{ height: 50, width: 50 }} /> {/* Đặt kích thước phù hợp */}
            <Text>Quantity: {item.quantity}</Text>
          </View>
        )}
      />
    </View>
  );
}

export default Cart;
