import { useState } from "react";
import { FlatList, TouchableOpacity, View, Text, Image } from "react-native";

function Content(props) {
  const [product, setproduct] = useState([
    {
      name: "Samsung Galaxy A05s",
      url: "https://cdn.tgdd.vn/Products/Images/42/317530/samsung-galaxy-a05s-sliver-thumb-600x600.jpeg",
      price: "4.290.000₫",
      specification: ["Chip Snapdragon 680", "RAM: 6 GB", "Dung lượng: 128 GB"],
    },
    {
      name: "Samsung Galaxy A05s",
      url: "https://cdn.tgdd.vn/Products/Images/42/317530/samsung-galaxy-a05s-sliver-thumb-600x600.jpeg",
      price: "4.290.000₫",
      specification: ["Chip Snapdragon 680", "RAM: 6 GB", "Dung lượng: 128 GB"],
    },
    {
      name: "Samsung Galaxy A05s",
      url: "https://cdn.tgdd.vn/Products/Images/42/317530/samsung-galaxy-a05s-sliver-thumb-600x600.jpeg",
      price: "4.290.000₫",
      specification: ["Chip Snapdragon 680", "RAM: 6 GB", "Dung lượng: 128 GB"],
    },
    {
      name: "Samsung Galaxy A05s",
      url: "https://cdn.tgdd.vn/Products/Images/42/317530/samsung-galaxy-a05s-sliver-thumb-600x600.jpeg",
      price: "4.290.000₫",
      specification: ["Chip Snapdragon 680", "RAM: 6 GB", "Dung lượng: 128 GB"],
    },
    {
      name: "Samsung Galaxy A05s",
      url: "https://cdn.tgdd.vn/Products/Images/42/317530/samsung-galaxy-a05s-sliver-thumb-600x600.jpeg",
      price: "4.290.000₫",
      specification: ["Chip Snapdragon 680", "RAM: 6 GB", "Dung lượng: 128 GB"],
    },
    {
      name: "Samsung Galaxy A05s",
      url: "https://cdn.tgdd.vn/Products/Images/42/317530/samsung-galaxy-a05s-sliver-thumb-600x600.jpeg",
      price: "4.290.000₫",
      specification: ["Chip Snapdragon 680", "RAM: 6 GB", "Dung lượng: 128 GB"],
    },
  ]);
  return (
    <View
      style={{
        width: "100%",
        backgroundColor: "white",
        flex: 70,
      }}
    >
      <FlatList
        numColumns={2}
        data={product}
        renderItem={({ item, index }) => (
          <TouchableOpacity
            style={{
              flex: 1,
              // backgroundColor: index % 2 == 0 ? 'green' : 'red',
              //height: 200,
              marginLeft: index % 2 == 0 ? 8 : 2,
              // height:200,
              marginTop: 5,
              marginRight: index % 2 == 0 ? 2 : 8,
              marginBottom: 5,
              borderRadius: 20,
              borderWidth: 1,
              borderColor: "black",
            }}
          >
            <Image
              source={{
                uri: item.url,
              }}
              style={{
                height: 100,
                width: 100,
                borderRadius: 10,
                margin: 10,
                resizeMode: "cover",
              }}
            />
            <Text style={{ color: "black" }}>{item.name}</Text>
            <Text style={{ color: "black", marginStart: 10 }}>
              {item.price}
            </Text>
            {item.specification.map((specification) => (
              <Text
                key={item.specification}
                style={{
                  color: "black",
                  fontSize: 14,
                  marginHorizontal: 5,
                }}
              >
                -{specification}
              </Text>
            ))}
          </TouchableOpacity>
        )}
      />
    </View>
  );
}
export default Content;
