import React from 'react';
import { View, Text, FlatList, Image, TouchableOpacity } from 'react-native';

function Cart({ route, navigation }) {
  const { addedItems } = route.params || {};

  const removeFromCart = (item) => {
    // Implement logic to remove the item from the cart
    // For simplicity, let's assume global.mycart is your cart data
    const updatedCart = global.mycart.filter(cartItem => cartItem.name !== item.name);
    global.mycart = updatedCart;
  };

  return (
    <View style={styles.container}>
      <Text style={styles.cartTitle}>Shopping Cart</Text>
      <FlatList
        data={addedItems || []}
        keyExtractor={(item) => item.name}
        renderItem={renderCartItem}
        style={styles.cartList}
      />
      {addedItems.length > 0 && (
        <TouchableOpacity onPress={() => navigation.navigate('Checkout')} style={styles.checkoutButton}>
          <Text style={styles.checkoutButtonText}>Proceed to Checkout</Text>
        </TouchableOpacity>
      )}
    </View>
  );
};
const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 15,
    backgroundColor: '#fff',
  },
  cartTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  cartList: {
    marginBottom: 10,
  },
  cartItemContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 10,
    borderWidth: 1,
    borderRadius: 8,
    padding: 10,
  },
  cartItemImage: {
    height: 50,
    width: 50,
    borderRadius: 5,
  },
  cartItemDetails: {
    flex: 1,
    marginLeft: 10,
  },
  cartItemName: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  removeButton: {
    backgroundColor: 'red',
    padding: 8,
    borderRadius: 5,
  },
  removeButtonText: {
    color: '#fff',
  },
  checkoutButton: {
    backgroundColor: 'green',
    padding: 15,
    borderRadius: 8,
    alignItems: 'center',
  },
  checkoutButtonText: {
    color: '#fff',
    fontWeight: 'bold',
  },
});

export default Cart;
