import React, { useState } from 'react';
import { View, Text, Image, ScrollView, TouchableOpacity } from 'react-native';
import { useNavigation } from '@react-navigation/native';

function ProductDetail({ route }) {
  const navigation = useNavigation();
  const { name, url, price } = route.params;
  const [count, setCount] = useState(1);

  const addToCart = () => {
    global.mycart = global.mycart || [];
    const newItem = {
      name,
      url,
      price,
      quantity: count,
    };
    setCount(1);

    global.mycart.push(newItem);
    console.log('Cart after adding item:', global.mycart);  
  };
  const goToCart = () => {
    navigation.navigate('Cart'); // Chuyển hướng đến màn hình 'Cart'
  };

  return (
    <ScrollView>
      <View style={{ flex: 30, borderWidth: 2, borderRadius: 10, marginHorizontal: 10 }}>
        <Image source={{ uri: url }} style={{ height: 200, width: '100%', resizeMode: 'center' }} />
        <Text>Name: {name}</Text>
        <Text>Price: ${price}</Text>

      </View>

      <View>
        <TouchableOpacity
          onPress={addToCart}

          style={{
            backgroundColor: 'blue',
            justifyContent: 'center',
            alignItems: 'center',
            borderWidth: 2,
            borderRadius: 15,
            height: 20,
            marginHorizontal: 50,
            marginVertical: 10,
          }}
        >
          <Text style={{ color: 'white' }}>Add to cart</Text>
        </TouchableOpacity>


      </View>
    </ScrollView>
  );
}

export default ProductDetail;
